definition.script:
node {
    checkout([$class: 'MercurialSCM', source: 'http://localhost:8000/'])
    def m3 = tool 'M3'
    configFileProvider([configFile(fileId: 'jenkins-mirror', variable: 'SETTINGS')]) {
        ansiColor('xterm') {
            sh "$m3/bin/mvn -s \$SETTINGS javadoc:javadoc"
            publishHTML([reportDir: 'target/site/apidocs/', reportName: 'Javadoc', reportTitles: 'Javadoc', reportFiles: 'overview-summary.html'])
        }
    }
}
