definition.script:
node {
    git 'https://github.com/jglick/simple-maven-project-with-tests'
    def m3 = tool 'M3'
    configFileProvider([configFile(fileId: 'jenkins-mirror', variable: 'SETTINGS')]) {
        ansiColor('xterm') {
            sh "$m3/bin/mvn -s \$SETTINGS -Dmaven.test.failure.ignore install"
        }
    }
    junit '**/target/surefire-reports/TEST-*.xml'
}
